        let url = "https://api.openai.com/v1/completions";
        let response = self.client.post(url)
        .bearer_auth(&self.api_key)
        .json(&serde_json::json!({
            "model": "gpt-3.5",
            "prompt": prompt,
            "max_tokens": 100,
        }))
        .send()
        .await?
        .text()
        .await?;
